import React, { useState } from 'react';
import Input from '../components/Input';
import Button from '../components/Button';
import Alert from '../components/Alert';

const Booking = () => {
  const [formData, setFormData] = useState({
    fullName: '',
    email: '',
    phoneNumber: '',
    departureLocation: '',
    destination: '',
    travelDate: '',
    ticketType: 'economy'
  });

  const [errors, setErrors] = useState({});
  const [alert, setAlert] = useState({ type: '', message: '', show: false });
  const [isSubmitting, setIsSubmitting] = useState(false);

  const validateForm = () => {
    const newErrors = {};

    if (!formData.fullName.trim()) {
      newErrors.fullName = 'Full name is required';
    }

    if (!formData.email.trim()) {
      newErrors.email = 'Email is required';
    } else if (!/\S+@\S+\.\S+/.test(formData.email)) {
      newErrors.email = 'Email is invalid';
    }

    if (!formData.phoneNumber.trim()) {
      newErrors.phoneNumber = 'Phone number is required';
    } else if (!/^\+?[\d\s-()]+$/.test(formData.phoneNumber)) {
      newErrors.phoneNumber = 'Phone number is invalid';
    }

    if (!formData.departureLocation.trim()) {
      newErrors.departureLocation = 'Departure location is required';
    }

    if (!formData.destination.trim()) {
      newErrors.destination = 'Destination is required';
    }

    if (!formData.travelDate) {
      newErrors.travelDate = 'Travel date is required';
    } else {
      const selectedDate = new Date(formData.travelDate);
      const today = new Date();
      today.setHours(0, 0, 0, 0);

      if (selectedDate < today) {
        newErrors.travelDate = 'Travel date cannot be in the past';
      }
    }

    setErrors(newErrors);
    return Object.keys(newErrors).length === 0;
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));

    // Clear error for this field when user starts typing
    if (errors[name]) {
      setErrors(prev => ({
        ...prev,
        [name]: ''
      }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();

    if (!validateForm()) {
      return;
    }

    setIsSubmitting(true);

    try {
      const response = await fetch('http://127.0.0.1:5000/api/bookings', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          fullName: formData.fullName,
          email: formData.email,
          phoneNumber: formData.phoneNumber,
          departureLocation: formData.departureLocation,
          destination: formData.destination,
          travelDate: formData.travelDate,
          ticketType: formData.ticketType
        }),
      });

      const data = await response.json();

      if (data.success) {
        setAlert({
          type: 'success',
          message: `Booking created successfully! Your Booking ID: #${data.data.bookingId}`,
          show: true
        });

        // Reset form
        setFormData({
          fullName: '',
          email: '',
          phoneNumber: '',
          departureLocation: '',
          destination: '',
          travelDate: '',
          ticketType: 'economy'
        });
      } else {
        throw new Error(data.message || 'Failed to create booking');
      }

    } catch (error) {
      console.error('Booking error:', error);
      setAlert({
        type: 'error',
        message: error.message || 'Failed to send booking request. Please try again.',
        show: true
      });
    } finally {
      setIsSubmitting(false);
    }
  };

  const closeAlert = () => {
    setAlert(prev => ({ ...prev, show: false }));
  };

  return (
    <div className="container mx-auto px-4 py-8">
      <div className="max-w-2xl mx-auto">
        <h1 className="text-3xl md:text-4xl font-bold text-center mb-8 text-gray-800">
          Book Your Ticket
        </h1>

        {alert.show && (
          <div className="mb-6">
            <Alert
              type={alert.type}
              message={alert.message}
              onClose={closeAlert}
            />
          </div>
        )}

        <div className="bg-white rounded-lg shadow-lg p-6 md:p-8">
          <form onSubmit={handleSubmit}>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              <Input
                label="Full Name"
                name="fullName"
                value={formData.fullName}
                onChange={handleChange}
                error={errors.fullName}
                required
                placeholder="Enter your full name"
              />

              <Input
                label="Email"
                type="email"
                name="email"
                value={formData.email}
                onChange={handleChange}
                error={errors.email}
                required
                placeholder="your.email@example.com"
              />

              <Input
                label="Phone Number"
                type="tel"
                name="phoneNumber"
                value={formData.phoneNumber}
                onChange={handleChange}
                error={errors.phoneNumber}
                required
                placeholder="+1 (555) 123-4567"
              />

              <div className="mb-4">
                <label className="block text-sm font-medium text-gray-700 mb-1">
                  Ticket Type
                </label>
                <select
                  name="ticketType"
                  value={formData.ticketType}
                  onChange={handleChange}
                  className="w-full px-3 py-2 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-primary-500 focus:border-transparent"
                >
                  <option value="economy">Economy</option>
                  <option value="business">Business</option>
                  <option value="premium">Premium</option>
                </select>
              </div>
            </div>

            <Input
              label="Departure Location"
              name="departureLocation"
              value={formData.departureLocation}
              onChange={handleChange}
              error={errors.departureLocation}
              required
              placeholder="Enter departure city or location"
            />

            <Input
              label="Destination"
              name="destination"
              value={formData.destination}
              onChange={handleChange}
              error={errors.destination}
              required
              placeholder="Enter destination city or location"
            />

            <Input
              label="Travel Date"
              type="date"
              name="travelDate"
              value={formData.travelDate}
              onChange={handleChange}
              error={errors.travelDate}
              required
              min={new Date().toISOString().split('T')[0]}
            />

            <div className="mt-6">
              <Button
                type="submit"
                disabled={isSubmitting}
                className="w-full"
                size="lg"
              >
                {isSubmitting ? 'Sending...' : 'Book Ticket'}
              </Button>
            </div>
          </form>

          <div className="mt-6 p-4 bg-blue-50 rounded-lg">
            <p className="text-sm text-blue-800">
              <strong>Note:</strong> Your booking will be saved in our database.
              Confirmation will be sent if the email service is configured.
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Booking;
